import React, { useState } from "react";
import Footer from "../components/Footer";
import DropDown from "../components/DropDown";

function BookingPage() {
  const [formValues, setFormValues] = useState({
    name: "",
    email: "",
    month: "",
    day: "",
    year: "",
    hour: "",
    minute: "",
    ampm: "AM",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormValues({
      ...formValues,
      [name]: value,
    });
  };

  const handleSelection = (value, name) => {
    setFormValues({ ...formValues, name: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert("Form Submitted", formValues);
  };

  const generateOptions = (start, end, pad = false) => {
    const options = [];
    for (let i = start; i <= end; i++) {
      const value = pad && i < 10 ? "0" + i : i.toString();
      options.push(
        <option key={value} value={value}>
          {value}
        </option>,
      );
    }
    return options;
  };

  const currentYear = new Date().getFullYear();

  return (
    <>
      <div className="bookingpage__hero">
        <img className="logo" src="./images/logo.svg" alt="brand logo" />
        <div className="hero-content">
          <h1>Reservations</h1>
          <p>
            We can’t wait to host you. If you have any special requirements
            please feel free to call on the phone number below. We’ll be happy
            to accommodate you.
          </p>
          <button className="mobile">reserve place</button>
        </div>
      </div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          id="name"
          name="name"
          value={formValues.name}
          onChange={handleChange}
          placeholder="Name"
          required
        />
        <input
          type="email"
          id="email"
          name="email"
          value={formValues.email}
          onChange={handleChange}
          placeholder="Email"
          required
        />
        <div className="date">
          <span>Pick a date</span>
          <DropDown
            options={generateOptions(1, 12, true)}
            onSelect={(value) => setFormValues({ ...formValues, month: value })}
          />
          <DropDown
            options={generateOptions(1, 31, true)}
            onSelect={(value) => setFormValues({ ...formValues, day: value })}
          />
          <DropDown
            options={generateOptions(currentYear, 1900)}
            onSelect={(value) => setFormValues({ ...formValues, year: value })}
          />
        </div>
        <div className="time">
          <span>Pick a time</span>
          <DropDown
            options={generateOptions(1, 12, true)}
            onSelect={(value) => setFormValues({ ...formValues, hour: value })}
          />
          <DropDown
            options={["00", "30"]}
            onSelect={(value) =>
              setFormValues({ ...formValues, minute: value })
            }
          />
          <DropDown
            options={["AM", "PM"]}
            onSelect={(value) => setFormValues({ ...formValues, ampm: value })}
          />
        </div>
      </form>
      <Footer />
    </>
  );
}

export default BookingPage;
